rt = require"RtAudio"print("VERSION",rt.getVersion())local compiledapis = rt.getCompiledApi()for k,v in pairs(compiledapis) do	print("API",k,v)endfor k,api in pairs(compiledapis) do	dac = rt.RtAudio(rt[api])	print"-------------------------------------"	print("current api",dac:getCurrentApi())	print("device count ",dac:getDeviceCount())	print("getdefaultoutput",dac:getDefaultOutputDevice())	print("getdefaultinput",dac:getDefaultInputDevice())		print"list devices:"	for i=0,dac:getDeviceCount()-1 do		print("\nDevice ",i)		for k,v in pairs(dac:getDeviceInfo(i)) do			if type(v)=="table" then				print("\t",k)				for k2,v2 in pairs(v) do					print("\t",k2,v2)				end			else				print("\t",k,v)			end		end	endend