fileName = [[C:\Users\victor\Desktop\musicas victor\resonator.wav]] --use your stereo filert = require"RtAudio"print("VERSION",rt.getVersion())dac = rt.RtAudio(rt.RtAudio_WINDOWS_DS)print("current api",dac:getCurrentApi())print("device count ",dac:getDeviceCount())print("getdefaultoutput",dac:getDefaultOutputDevice())print("getdefaultinput",dac:getDefaultInputDevice())--list devicesfor i=0,dac:getDeviceCount()-1 do	print("\nDevice ",i)	for k,v in pairs(dac:getDeviceInfo(i)) do		if type(v)=="table" then			print(k)			for k2,v2 in pairs(v) do				print(k2,v2)			end		else			print(k,v)		end	endendprint"------------------------------------------"-- openStream({outputdevice, nchannels},{inputdevice,nchannels},samplerate, desired_buffer_size)busiz = dac:openStream({0,2},nil,44100,1024)dac:record("record.wav")print("buffersize",busiz)soundFiles = {}for i=1,5 do	soundFiles[i] = rt.soundFile(fileName)	soundFiles[i]:play(dac,0.5/i,1*(i-1)) --RtAudio, level , timeoffsetenddac:startStream()print"press return to setStreamTime 2"io.read"*l"dac:setStreamTime(2)print"press return to end"io.read"*l"