rt = require"RtAudio"dac = rt.RtAudio(rt.RtAudio_WINDOWS_DS)local function xxx()	local om = 0	local sin = math.sin	return function(nFrames)		local res = {}		local hh = 2*math.pi*200/44100		for i=1,nFrames do			om = om + hh			local val = sin(om)*sin(om*0.01)			table.insert(res,val)			table.insert(res,val)		end		return res	endenddac:setCallback(string.dump(xxx))dod = dac:getDefaultOutputDevice()busiz = dac:openStream({dod,2},nil,44100,1024)dac:startStream()print"press return to exit"io.read"*l"dac:closeStream()