rt = require"RtAudio"dac = rt.RtAudio(rt.RtAudio_WINDOWS_DS)chunk = [[		local om = 0	return function(nFrames)		--print"nFrames"		local res = {}		local sin = math.sin		local hh = 2*math.pi*200/44100		for i=1,nFrames do			om = om + hh			local val = sin(om)*sin(om*0.01)			table.insert(res,val)			table.insert(res,val)		end		return res	end]]--dac:setCallback(string.dump(loadstring(chunk)))dac:setCallback(chunk)local function caray()	local om = 0	return function(nFrames)		local res = {}		local sin = math.sin		local hh = 2*math.pi*200/44100		for i=1,nFrames do			om = om + hh			local val = sin(om)*sin(om*0.01)			table.insert(res,val)			table.insert(res,val)		end		return res	endend--assert(loadstring(string.dump(caray)))--dac:setCallback(string.dump(caray))busiz = dac:openStream({0,2},nil,44100,1024)dac:startStream()print"press return to exit"io.read"*l"dac:closeStream()