rt = require"RtAudio"dac = rt.RtAudio(rt.RtAudio_WINDOWS_DS)function fun1(rtlinda)	local om = 0	local freq = 200	local sin = math.sin	local ffi = require ("ffi")	return function(nFrames,buffer)		local key,val = rtlinda:receive(0,"freq")		freq = key and val or freq		local buffer = ffi.cast("double *", buffer)		local hh = 2*math.pi*freq/44100		for i=0,2*nFrames-1,2 do			om = om + hh			local val = sin(om)*sin(om*0.01)			buffer[i] = val			buffer[i+1] = val		end	endenddac:setcb_lanes(fun1)busiz = dac:openStream({0,2},nil,44100,512,true) --true for using ffi buffer--dac:record("record.wav")dac:startStream()while true do	print"type freq in Hz or quit to exit"	freq = io.read"*l"	if freq == "quit" then break end	rt.linda:send("freq",tonumber(freq) or 200)end